import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  ShoppingCart, 
  Package, 
  Star, 
  TrendingUp, 
  Heart,
  Filter
} from "lucide-react";
import SweetGrid from "./SweetGrid";
import { DashboardSkeleton } from "./LoadingStates";
import { FadeIn, SlideIn, StaggerContainer, StaggerItem } from "./PageTransition";

interface Sweet {
  id: string;
  name: string;
  price: number;
  image: string;
  category: string;
  quantity: number;
  rating?: number;
  description?: string;
}

interface DashboardProps {
  user: {
    username: string;
    isAdmin: boolean;
  };
  sweets: Sweet[];
  cartItems: string[];
  favoriteItems: string[];
  onPurchase: (id: string) => void;
  onViewDetails: (id: string) => void;
  onToggleFavorite: (id: string) => void;
  isLoading?: boolean;
}

export default function Dashboard({ 
  user, 
  sweets, 
  cartItems, 
  favoriteItems,
  onPurchase, 
  onViewDetails,
  onToggleFavorite,
  isLoading = false
}: DashboardProps) {
  const [showFavorites, setShowFavorites] = useState(false);

  const featuredSweets = sweets.filter(sweet => sweet.rating && sweet.rating >= 4.7).slice(0, 4);
  const newArrivals = sweets.slice(-4);
  const displaySweets = showFavorites 
    ? sweets.filter(sweet => favoriteItems.includes(sweet.id))
    : sweets;

  const totalProducts = sweets.length;
  const averageRating = sweets.reduce((sum, sweet) => sum + (sweet.rating || 0), 0) / sweets.length;
  const categoriesCount = new Set(sweets.map(sweet => sweet.category)).size;

  if (isLoading) {
    return <DashboardSkeleton />;
  }

  return (
    <div className="container mx-auto p-4 space-y-6">
      {/* Welcome Section */}
      <SlideIn direction="down" className="bg-gradient-to-r from-primary/10 to-primary/5 rounded-lg p-6">
        <div className="max-w-2xl">
          <h1 className="text-2xl sm:text-3xl font-bold mb-2" data-testid="text-welcome">
            Welcome back, {user.username}! ðŸ­
          </h1>
          <p className="text-muted-foreground text-base sm:text-lg">
            Discover delicious sweets and manage your candy cravings. 
            {user.isAdmin && " You have admin access to manage the store inventory."}
          </p>
        </div>
      </SlideIn>

      {/* Quick Stats */}
      <StaggerContainer className="grid grid-cols-1 md:grid-cols-3 gap-4" staggerDelay={0.1}>
        <StaggerItem>
          <Card className="hover-elevate transition-all duration-200">
            <CardContent className="p-4 sm:p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Available Products</p>
                  <p className="text-xl sm:text-2xl font-bold" data-testid="stat-products">{totalProducts}</p>
                </div>
                <Package className="w-6 h-6 sm:w-8 sm:h-8 text-primary" />
              </div>
            </CardContent>
          </Card>
        </StaggerItem>

        <StaggerItem>
          <Card className="hover-elevate transition-all duration-200">
            <CardContent className="p-4 sm:p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Average Rating</p>
                  <p className="text-xl sm:text-2xl font-bold" data-testid="stat-rating">
                    {averageRating.toFixed(1)} â­
                  </p>
                </div>
                <Star className="w-6 h-6 sm:w-8 sm:h-8 text-yellow-500" />
              </div>
            </CardContent>
          </Card>
        </StaggerItem>

        <StaggerItem>
          <Card className="hover-elevate transition-all duration-200">
            <CardContent className="p-4 sm:p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Categories</p>
                  <p className="text-xl sm:text-2xl font-bold" data-testid="stat-categories">{categoriesCount}</p>
                </div>
                <TrendingUp className="w-6 h-6 sm:w-8 sm:h-8 text-green-600" />
              </div>
            </CardContent>
          </Card>
        </StaggerItem>
      </StaggerContainer>

      {/* Featured Products */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Star className="w-5 h-5 text-yellow-500" />
            Featured Products
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
            {featuredSweets.map((sweet) => (
              <div key={sweet.id} className="group cursor-pointer" onClick={() => onViewDetails(sweet.id)}>
                <div className="aspect-square mb-3 overflow-hidden rounded-md bg-muted">
                  <img
                    src={sweet.image}
                    alt={sweet.name}
                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-200"
                  />
                </div>
                <div className="space-y-1">
                  <h4 className="font-medium line-clamp-1">{sweet.name}</h4>
                  <div className="flex items-center justify-between">
                    <span className="text-lg font-semibold">${sweet.price.toFixed(2)}</span>
                    <Badge variant="secondary">{sweet.category}</Badge>
                  </div>
                  <div className="flex items-center gap-1">
                    <Star className="w-3 h-3 fill-yellow-400 text-yellow-400" />
                    <span className="text-sm text-muted-foreground">{sweet.rating}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* New Arrivals */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Package className="w-5 h-5 text-primary" />
            New Arrivals
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
            {newArrivals.map((sweet) => (
              <div key={sweet.id} className="group cursor-pointer" onClick={() => onViewDetails(sweet.id)}>
                <div className="aspect-square mb-3 overflow-hidden rounded-md bg-muted relative">
                  <img
                    src={sweet.image}
                    alt={sweet.name}
                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-200"
                  />
                  <Badge className="absolute top-2 left-2" variant="secondary">
                    New
                  </Badge>
                  <Button
                    size="icon"
                    variant="ghost"
                    className="absolute top-2 right-2 bg-background/80 hover:bg-background"
                    onClick={(e) => {
                      e.stopPropagation();
                      onToggleFavorite(sweet.id);
                    }}
                    data-testid={`button-favorite-${sweet.id}`}
                  >
                    <Heart 
                      className={`w-4 h-4 ${favoriteItems.includes(sweet.id) ? 'fill-red-500 text-red-500' : ''}`} 
                    />
                  </Button>
                </div>
                <div className="space-y-1">
                  <h4 className="font-medium line-clamp-1">{sweet.name}</h4>
                  <div className="flex items-center justify-between">
                    <span className="text-lg font-semibold">${sweet.price.toFixed(2)}</span>
                    <Badge variant="secondary">{sweet.category}</Badge>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Action Buttons */}
      <FadeIn delay={0.6} className="flex flex-col sm:flex-row items-center gap-4 justify-center">
        <Button
          variant={showFavorites ? "default" : "outline"}
          onClick={() => setShowFavorites(!showFavorites)}
          className="gap-2 w-full sm:w-auto"
          data-testid="button-toggle-favorites"
        >
          <Heart className={`w-4 h-4 ${showFavorites ? 'fill-current' : ''}`} />
          <span className="hidden sm:inline">
            {showFavorites ? 'Show All Products' : 'Show Favorites Only'}
          </span>
          <span className="sm:hidden">
            {showFavorites ? 'All Products' : 'Favorites'}
          </span>
        </Button>

        <Button
          variant="outline"
          className="gap-2 w-full sm:w-auto"
          data-testid="button-view-cart"
        >
          <ShoppingCart className="w-4 h-4" />
          <span>Cart ({cartItems.length})</span>
        </Button>
      </FadeIn>

      {/* All Products Grid */}
      <SweetGrid
        sweets={displaySweets}
        onPurchase={onPurchase}
        onViewDetails={onViewDetails}
      />
    </div>
  );
}

client/src/components/EnhancedSearch.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { 
  Search, 
  X, 
  Clock, 
  TrendingUp,
  Hash,
  Keyboard
} from "lucide-react";

interface Sweet {
  id: string;
  name: string;
  price: number;
  image: string;
  category: string;
  quantity: number;
  rating?: number;
  description?: string;
}

interface EnhancedSearchProps {
  sweets: Sweet[];
  onSearch: (query: string) => void;
  placeholder?: string;
  showSuggestions?: boolean;
  className?: string;
}

export default function EnhancedSearch({ 
  sweets, 
  onSearch, 
  placeholder = "Search sweets, categories, or descriptions...",
  showSuggestions = true,
  className = ""
}: EnhancedSearchProps) {
  const [query, setQuery] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [searchHistory, setSearchHistory] = useState<string[]>([]);
  const [focusedIndex, setFocusedIndex] = useState(-1);
  const inputRef = useRef<HTMLInputElement>(null);
  const suggestionRefs = useRef<(HTMLDivElement | null)[]>([]);

  // Get search suggestions
  const getSuggestions = () => {
    if (!query.trim()) {
      return {
        products: [],
        categories: [],
        recent: searchHistory.slice(0, 3)
      };
    }

    const queryLower = query.toLowerCase();

    const products = sweets
      .filter(sweet => 
        sweet.name.toLowerCase().includes(queryLower) ||
        sweet.description?.toLowerCase().includes(queryLower)
      )
      .slice(0, 5);

    const categories = Array.from(new Set(
      sweets
        .filter(sweet => sweet.category.toLowerCase().includes(queryLower))
        .map(sweet => sweet.category)
    )).slice(0, 3);

    const recent = searchHistory
      .filter(term => term.toLowerCase().includes(queryLower))
      .slice(0, 3);

    return { products, categories, recent };
  };

  const suggestions = getSuggestions();
  const allSuggestions = [
    ...suggestions.recent.map(term => ({ type: 'recent' as const, value: term })),
    ...suggestions.categories.map(cat => ({ type: 'category' as const, value: cat })),
    ...suggestions.products.map(product => ({ type: 'product' as const, value: product.name, product }))
  ];

  // Handle search execution
  const executeSearch = (searchTerm: string) => {
    if (searchTerm.trim()) {
      onSearch(searchTerm);

      // Add to search history
      setSearchHistory(prev => {
        const filtered = prev.filter(term => term !== searchTerm);
        return [searchTerm, ...filtered].slice(0, 10);
      });
    } else {
      onSearch("");
    }
    setIsOpen(false);
    setFocusedIndex(-1);
  };

  // Handle input change
  const handleInputChange = (value: string) => {
    setQuery(value);
    setIsOpen(value.length > 0 || showSuggestions);
    setFocusedIndex(-1);

    // Real-time search for immediate feedback
    onSearch(value);
  };

  // Handle keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!isOpen) return;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setFocusedIndex(prev => 
          prev < allSuggestions.length - 1 ? prev + 1 : prev
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setFocusedIndex(prev => prev > 0 ? prev - 1 : -1);
        break;
      case 'Enter':
        e.preventDefault();
        if (focusedIndex >= 0 && allSuggestions[focusedIndex]) {
          const suggestion = allSuggestions[focusedIndex];
          setQuery(suggestion.value);
          executeSearch(suggestion.value);
        } else {
          executeSearch(query);
        }
        break;
      case 'Escape':
        setIsOpen(false);
        setFocusedIndex(-1);
        inputRef.current?.blur();
        break;
    }
  };

  // Handle suggestion click
  const handleSuggestionClick = (suggestion: { type: string; value: string; product?: Sweet }) => {
    setQuery(suggestion.value);
    executeSearch(suggestion.value);
  };

  // Clear search
  const clearSearch = () => {
    setQuery("");
    onSearch("");
    setIsOpen(false);
    inputRef.current?.focus();
  };

  // Clear search history
  const clearHistory = () => {
    setSearchHistory([]);
  };

  // Focus management
  useEffect(() => {
    if (focusedIndex >= 0 && suggestionRefs.current[focusedIndex]) {
      suggestionRefs.current[focusedIndex]?.scrollIntoView({
        block: 'nearest',
        behavior: 'smooth'
      });
    }
  }, [focusedIndex]);

  // Keyboard shortcut (Ctrl/Cmd + K)
  useEffect(() => {
    const handleGlobalKeydown = (e: KeyboardEvent) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
        e.preventDefault();
        inputRef.current?.focus();
        setIsOpen(true);
      }
    };

    document.addEventListener('keydown', handleGlobalKeydown);
    return () => document.removeEventListener('keydown', handleGlobalKeydown);
  }, []);

  return (
    <div className={`relative ${className}`}>
      <div className="relative">
        <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
        <Input
          ref={inputRef}
          type="search"
          placeholder={placeholder}
          value={query}
          onChange={(e) => handleInputChange(e.target.value)}
          onFocus={() => setIsOpen(true)}
          onKeyDown={handleKeyDown}
          className="pl-10 pr-16"
          data-testid="input-enhanced-search"
        />

        <div className="absolute right-2 top-1/2 -translate-y-1/2 flex items-center gap-1">
          {query && (
            <Button
              variant="ghost"
              size="icon"
              className="h-6 w-6"
              onClick={clearSearch}
              data-testid="button-clear-search"
            >
              <X className="w-3 h-3" />
            </Button>
          )}

          <Badge variant="outline" className="text-xs hidden sm:flex">
            <Keyboard className="w-3 h-3 mr-1" />
            âŒ˜K
          </Badge>
        </div>
      </div>

      {/* Search Suggestions Dropdown */}
      {isOpen && (showSuggestions || query) && (
        <Card className="absolute top-full left-0 right-0 mt-1 z-50 max-h-96 overflow-hidden">
          <CardContent className="p-0">
            <ScrollArea className="max-h-96">
              <div className="py-2">
                {allSuggestions.length === 0 && query ? (
                  <div className="px-4 py-3 text-sm text-muted-foreground text-center">
                    No suggestions found for "{query}"
                  </div>
                ) : (
                  <>
                    {/* Recent Searches */}
                    {suggestions.recent.length > 0 && !query && (
                      <div>
                        <div className="px-4 py-2 text-xs font-medium text-muted-foreground flex items-center justify-between">
                          <span className="flex items-center gap-2">
                            <Clock className="w-3 h-3" />
                            Recent Searches
                          </span>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-auto p-1 text-xs"
                            onClick={clearHistory}
                            data-testid="button-clear-history"
                          >
                            Clear
                          </Button>
                        </div>
                        {suggestions.recent.map((term, index) => (
                          <div
                            key={`recent-${term}`}
                            ref={el => suggestionRefs.current[index] = el}
                            className={`px-4 py-2 cursor-pointer hover:bg-muted flex items-center gap-2 ${
                              focusedIndex === index ? 'bg-muted' : ''
                            }`}
                            onClick={() => handleSuggestionClick({ type: 'recent', value: term })}
                            data-testid={`suggestion-recent-${index}`}
                          >
                            <Clock className="w-4 h-4 text-muted-foreground" />
                            <span>{term}</span>
                          </div>
                        ))}
                      </div>
                    )}

                    {/* Category Suggestions */}
                    {suggestions.categories.length > 0 && (
                      <div>
                        <div className="px-4 py-2 text-xs font-medium text-muted-foreground border-t">
                          <span className="flex items-center gap-2">
                            <Hash className="w-3 h-3" />
                            Categories
                          </span>
                        </div>
                        {suggestions.categories.map((category, index) => {
                          const suggestionIndex = suggestions.recent.length + index;
                          return (
                            <div
                              key={`category-${category}`}
                              ref={el => suggestionRefs.current[suggestionIndex] = el}
                              className={`px-4 py-2 cursor-pointer hover:bg-muted flex items-center gap-2 ${
                                focusedIndex === suggestionIndex ? 'bg-muted' : ''
                              }`}
                              onClick={() => handleSuggestionClick({ type: 'category', value: category })}
                              data-testid={`suggestion-category-${index}`}
                            >
                              <Hash className="w-4 h-4 text-muted-foreground" />
                              <span>{category}</span>
                            </div>
                          );
                        })}
                      </div>
                    )}

                    {/* Product Suggestions */}
                    {suggestions.products.length > 0 && (
                      <div>
                        <div className="px-4 py-2 text-xs font-medium text-muted-foreground border-t">
                          <span className="flex items-center gap-2">
                            <TrendingUp className="w-3 h-3" />
                            Products
                          </span>
                        </div>
                        {suggestions.products.map((product, index) => {
                          const suggestionIndex = suggestions.recent.length + suggestions.categories.length + index;
                          return (
                            <div
                              key={`product-${product.id}`}
                              ref={el => suggestionRefs.current[suggestionIndex] = el}
                              className={`px-4 py-2 cursor-pointer hover:bg-muted flex items-center gap-3 ${
                                focusedIndex === suggestionIndex ? 'bg-muted' : ''
                              }`}
                              onClick={() => handleSuggestionClick({ type: 'product', value: product.name, product })}
                              data-testid={`suggestion-product-${index}`}
                            >
                              <div className="w-8 h-8 rounded bg-muted flex-shrink-0 overflow-hidden">
                                <img
                                  src={product.image}
                                  alt={product.name}
                                  className="w-full h-full object-cover"
                                />
                              </div>
                              <div className="flex-1 min-w-0">
                                <p className="text-sm font-medium truncate">{product.name}</p>
                                <div className="flex items-center gap-2 text-xs text-muted-foreground">
                                  <span>{product.category}</span>
                                  <span>â€¢</span>
                                  <span>${product.price.toFixed(2)}</span>
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    )}
                  </>
                )}
              </div>
            </ScrollArea>
          </CardContent>
        </Card>
      )}

      {/* Click outside to close */}
      {isOpen && (
        <div
          className="fixed inset-0 z-40"
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
}

client/src/components/Header.tsx.content:
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { 
  Candy, 
  Search, 
  ShoppingCart, 
  User, 
  Settings, 
  LogOut,
  Moon,
  Sun
} from "lucide-react";
import { useState } from "react";

interface HeaderProps {
  user?: {
    username: string;
    isAdmin: boolean;
  };
  cartItemCount?: number;
  onSearch: (query: string) => void;
  onCartClick: () => void;
  onProfileClick: () => void;
  onLogout: () => void;
  onToggleTheme: () => void;
  isDarkMode: boolean;
}

export default function Header({
  user,
  cartItemCount = 0,
  onSearch,
  onCartClick,
  onProfileClick,
  onLogout,
  onToggleTheme,
  isDarkMode
}: HeaderProps) {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    onSearch(searchQuery);
  };

  return (
    <header className="border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
      <div className="container mx-auto px-4 h-16 flex items-center justify-between gap-4">
        {/* Logo */}
        <div className="flex items-center gap-2 shrink-0">
          <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
            <Candy className="w-5 h-5 text-primary-foreground" />
          </div>
          <span className="font-semibold text-lg hidden sm:block" data-testid="text-logo">
            Sweet Shop
          </span>
        </div>

        {/* Enhanced Search - Only show on larger screens in header */}
        {user && (
          <div className="hidden md:block flex-1 max-w-md">
            <form onSubmit={handleSearch}>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                <Input
                  type="search"
                  placeholder="Search sweets..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-search"
                />
              </div>
            </form>
          </div>
        )}

        {/* Right side actions */}
        <div className="flex items-center gap-2">
          {/* Theme toggle */}
          <Button
            variant="ghost"
            size="icon"
            onClick={onToggleTheme}
            data-testid="button-theme-toggle"
          >
            {isDarkMode ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
          </Button>

          {user ? (
            <>
              {/* Cart */}
              <Button
                variant="ghost"
                size="icon"
                className="relative"
                onClick={onCartClick}
                data-testid="button-cart"
              >
                <ShoppingCart className="w-4 h-4" />
                {cartItemCount > 0 && (
                  <Badge 
                    variant="destructive" 
                    className="absolute -top-1 -right-1 h-5 w-5 rounded-full p-0 text-xs flex items-center justify-center"
                    data-testid="badge-cart-count"
                  >
                    {cartItemCount > 99 ? "99+" : cartItemCount}
                  </Badge>
                )}
              </Button>

              {/* User menu */}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" size="icon" data-testid="button-user-menu">
                    <User className="w-4 h-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-56">
                  <DropdownMenuLabel className="font-normal">
                    <div className="flex flex-col space-y-1">
                      <p className="text-sm font-medium leading-none" data-testid="text-username">
                        {user.username}
                      </p>
                      <p className="text-xs leading-none text-muted-foreground">
                        {user.isAdmin ? "Administrator" : "Customer"}
                      </p>
                    </div>
                  </DropdownMenuLabel>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={onProfileClick} data-testid="menu-profile">
                    <User className="mr-2 h-4 w-4" />
                    <span>Profile</span>
                  </DropdownMenuItem>
                  {user.isAdmin && (
                    <DropdownMenuItem data-testid="menu-admin">
                      <Settings className="mr-2 h-4 w-4" />
                      <span>Admin Panel</span>
                    </DropdownMenuItem>
                  )}
                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={onLogout} data-testid="menu-logout">
                    <LogOut className="mr-2 h-4 w-4" />
                    <span>Log out</span>
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </>
          ) : (
            <Button data-testid="button-login">
              Sign In
            </Button>
          )}
        </div>
      </div>
    </header>
  );
}

client/src/components/PageTransition.tsx.content:
import { motion, AnimatePresence } from "framer-motion";
import { useLocation } from "wouter";

interface PageTransitionProps {
  children: React.ReactNode;
}

const pageVariants = {
  initial: {
    opacity: 0,
    y: 20,
    scale: 0.98,
  },
  in: {
    opacity: 1,
    y: 0,
    scale: 1,
  },
  out: {
    opacity: 0,
    y: -20,
    scale: 1.02,
  },
};

const pageTransition = {
  type: "tween",
  ease: "anticipate",
  duration: 0.3,
};

export default function PageTransition({ children }: PageTransitionProps) {
  const [location] = useLocation();

  return (
    <AnimatePresence mode="wait" initial={false}>
      <motion.div
        key={location}
        initial="initial"
        animate="in"
        exit="out"
        variants={pageVariants}
        transition={pageTransition}
        className="h-full"
      >
        {children}
      </motion.div>
    </AnimatePresence>
  );
}

// Stagger animation for lists
export function StaggerContainer({ 
  children, 
  className = "",
  staggerDelay = 0.1 
}: { 
  children: React.ReactNode;
  className?: string;
  staggerDelay?: number;
}) {
  return (
    <motion.div
      className={className}
      initial="hidden"
      animate="visible"
      variants={{
        hidden: {},
        visible: {
          transition: {
            staggerChildren: staggerDelay,
          },
        },
      }}
    >
      {children}
    </motion.div>
  );
}

// Individual stagger item
export function StaggerItem({ 
  children, 
  className = "",
  delay = 0 
}: { 
  children: React.ReactNode;
  className?: string;
  delay?: number;
}) {
  return (
    <motion.div
      className={className}
      variants={{
        hidden: {
          opacity: 0,
          y: 20,
        },
        visible: {
          opacity: 1,
          y: 0,
          transition: {
            duration: 0.4,
            ease: "easeOut",
            delay,
          },
        },
      }}
    >
      {children}
    </motion.div>
  );
}

// Fade in animation
export function FadeIn({ 
  children, 
  className = "",
  delay = 0,
  duration = 0.5 
}: { 
  children: React.ReactNode;
  className?: string;
  delay?: number;
  duration?: number;
}) {
  return (
    <motion.div
      className={className}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration, delay, ease: "easeOut" }}
    >
      {children}
    </motion.div>
  );
}

// Scale in animation
export function ScaleIn({ 
  children, 
  className = "",
  delay = 0 
}: { 
  children: React.ReactNode;
  className?: string;
  delay?: number;
}) {
  return (
    <motion.div
      className={className}
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.4, delay, ease: "easeOut" }}
    >
      {children}
    </motion.div>
  );
}

// Slide in from direction
export function SlideIn({ 
  children, 
  className = "",
  direction = "up",
  delay = 0,
  distance = 20 
}: { 
  children: React.ReactNode;
  className?: string;
  direction?: "up" | "down" | "left" | "right";
  delay?: number;
  distance?: number;
}) {
  const getInitial = () => {
    switch (direction) {
      case "up":
        return { opacity: 0, y: distance };
      case "down":
        return { opacity: 0, y: -distance };
      case "left":
        return { opacity: 0, x: distance };
      case "right":
        return { opacity: 0, x: -distance };
      default:
        return { opacity: 0, y: distance };
    }
  };

  return (
    <motion.div
      className={className}
      initial={getInitial()}
      animate={{ opacity: 1, x: 0, y: 0 }}
      transition={{ duration: 0.4, delay, ease: "easeOut" }}
    >
      {children}
    </motion.div>
  );
}

client/src/components/ProductDetailModal.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Separator } from "@/components/ui/separator";
import { 
  Star, 
  ShoppingCart, 
  Heart, 
  Minus, 
  Plus,
  Package,
  X
} from "lucide-react";

interface Sweet {
  id: string;
  name: string;
  price: number;
  image: string;
  category: string;
  quantity: number;
  rating?: number;
  description?: string;
}

interface ProductDetailModalProps {
  sweet: Sweet | null;
  isOpen: boolean;
  onClose: () => void;
  onAddToCart: (sweetId: string, quantity: number) => void;
  onToggleFavorite: (sweetId: string) => void;
  isFavorite: boolean;
}

export default function ProductDetailModal({
  sweet,
  isOpen,
  onClose,
  onAddToCart,
  onToggleFavorite,
  isFavorite,
}: ProductDetailModalProps) {
  const [selectedQuantity, setSelectedQuantity] = useState(1);

  if (!sweet) return null;

  const isOutOfStock = sweet.quantity === 0;
  const isLowStock = sweet.quantity > 0 && sweet.quantity <= 5;

  const handleAddToCart = () => {
    onAddToCart(sweet.id, selectedQuantity);
    setSelectedQuantity(1);
  };

  const handleIncrement = () => {
    if (selectedQuantity < sweet.quantity) {
      setSelectedQuantity(prev => prev + 1);
    }
  };

  const handleDecrement = () => {
    if (selectedQuantity > 1) {
      setSelectedQuantity(prev => prev - 1);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="modal-product-detail">
        <DialogHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <DialogTitle className="text-2xl font-bold pr-8">
            {sweet.name}
          </DialogTitle>
          <Button
            variant="ghost"
            size="icon"
            onClick={onClose}
            className="absolute right-4 top-4"
            data-testid="button-close-detail"
          >
            <X className="w-4 h-4" />
          </Button>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Product Image */}
          <div className="space-y-4">
            <div className="aspect-square rounded-lg overflow-hidden bg-muted">
              <img
                src={sweet.image}
                alt={sweet.name}
                className="w-full h-full object-cover"
                data-testid="img-product-detail"
              />
            </div>

            {/* Product Stats */}
            <div className="grid grid-cols-3 gap-4 text-center">
              <div className="p-3 bg-muted rounded-lg">
                <Package className="w-5 h-5 mx-auto mb-1 text-muted-foreground" />
                <p className="text-sm font-medium">{sweet.quantity}</p>
                <p className="text-xs text-muted-foreground">In Stock</p>
              </div>
              <div className="p-3 bg-muted rounded-lg">
                <Star className="w-5 h-5 mx-auto mb-1 text-yellow-500" />
                <p className="text-sm font-medium">{sweet.rating || 'N/A'}</p>
                <p className="text-xs text-muted-foreground">Rating</p>
              </div>
              <div className="p-3 bg-muted rounded-lg">
                <ShoppingCart className="w-5 h-5 mx-auto mb-1 text-muted-foreground" />
                <p className="text-sm font-medium">${sweet.price}</p>
                <p className="text-xs text-muted-foreground">Price</p>
              </div>
            </div>
          </div>

          {/* Product Details */}
          <div className="space-y-6">
            {/* Price and Category */}
            <div className="flex items-center justify-between">
              <div>
                <p className="text-3xl font-bold" data-testid="text-detail-price">
                  ${sweet.price.toFixed(2)}
                </p>
                <div className="flex items-center gap-2 mt-1">
                  <Badge variant="secondary" data-testid="text-detail-category">
                    {sweet.category}
                  </Badge>
                  {sweet.rating && (
                    <div className="flex items-center gap-1">
                      <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                      <span className="text-sm text-muted-foreground" data-testid="text-detail-rating">
                        {sweet.rating}
                      </span>
                    </div>
                  )}
                </div>
              </div>

              <Button
                variant="outline"
                size="icon"
                onClick={() => onToggleFavorite(sweet.id)}
                data-testid="button-toggle-favorite-detail"
              >
                <Heart className={`w-4 h-4 ${isFavorite ? 'fill-red-500 text-red-500' : ''}`} />
              </Button>
            </div>

            {/* Stock Status */}
            <div>
              {isOutOfStock ? (
                <Badge variant="destructive" className="text-sm" data-testid="status-out-of-stock-detail">
                  Out of Stock
                </Badge>
              ) : isLowStock ? (
                <Badge variant="secondary" className="text-sm" data-testid="status-low-stock-detail">
                  Only {sweet.quantity} left in stock
                </Badge>
              ) : (
                <Badge variant="default" className="text-sm" data-testid="status-in-stock-detail">
                  {sweet.quantity} in stock
                </Badge>
              )}
            </div>

            {/* Description */}
            <div>
              <h3 className="font-semibold mb-2">Description</h3>
              <p className="text-muted-foreground leading-relaxed" data-testid="text-detail-description">
                {sweet.description || 'No description available for this product.'}
              </p>
            </div>

            <Separator />

            {/* Quantity Selector and Add to Cart */}
            {!isOutOfStock && (
              <div className="space-y-4">
                <div>
                  <label className="text-sm font-medium mb-2 block">Quantity</label>
                  <div className="flex items-center gap-3">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={handleDecrement}
                      disabled={selectedQuantity <= 1}
                      data-testid="button-decrease-detail"
                    >
                      <Minus className="w-4 h-4" />
                    </Button>

                    <span className="w-12 text-center font-medium" data-testid="text-selected-quantity">
                      {selectedQuantity}
                    </span>

                    <Button
                      variant="outline"
                      size="icon"
                      onClick={handleIncrement}
                      disabled={selectedQuantity >= sweet.quantity}
                      data-testid="button-increase-detail"
                    >
                      <Plus className="w-4 h-4" />
                    </Button>

                    <span className="text-sm text-muted-foreground ml-2">
                      of {sweet.quantity} available
                    </span>
                  </div>
                </div>

                <div className="space-y-3">
                  <p className="text-lg font-semibold" data-testid="text-total-price">
                    Total: ${(sweet.price * selectedQuantity).toFixed(2)}
                  </p>

                  <Button
                    onClick={handleAddToCart}
                    className="w-full gap-2"
                    size="lg"
                    data-testid="button-add-to-cart-detail"
                  >
                    <ShoppingCart className="w-4 h-4" />
                    Add {selectedQuantity} to Cart
                  </Button>
                </div>
              </div>
            )}

            {/* Additional Info */}
            <div className="text-xs text-muted-foreground space-y-1">
              <p>âœ“ Free shipping on orders over $25</p>
              <p>âœ“ 30-day satisfaction guarantee</p>
              <p>âœ“ Fresh ingredients, made daily</p>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/SweetCard.tsx.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardFooter } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ShoppingCart, Star } from "lucide-react";

interface SweetCardProps {
  id: string;
  name: string;
  price: number;
  image: string;
  category: string;
  quantity: number;
  rating?: number;
  description?: string;
  onPurchase: (id: string) => void;
  onViewDetails: (id: string) => void;
}

export default function SweetCard({
  id,
  name,
  price,
  image,
  category,
  quantity,
  rating = 4.5,
  description,
  onPurchase,
  onViewDetails,
}: SweetCardProps) {
  const isOutOfStock = quantity === 0;
  const isLowStock = quantity > 0 && quantity <= 5;

  return (
    <Card className="hover-elevate transition-all duration-200 h-full flex flex-col" data-testid={`card-sweet-${id}`}>
      <CardContent className="p-3 sm:p-4 flex-1 flex flex-col">
        <div className="aspect-square mb-4 overflow-hidden rounded-md">
          <img
            src={image}
            alt={name}
            className="w-full h-full object-cover cursor-pointer"
            onClick={() => onViewDetails(id)}
            data-testid={`img-sweet-${id}`}
          />
        </div>

        <div className="space-y-2 flex-1 flex flex-col">
          <div className="flex items-start justify-between gap-2">
            <h3 
              className="font-medium text-sm sm:text-base leading-tight cursor-pointer hover:text-primary line-clamp-2 flex-1"
              onClick={() => onViewDetails(id)}
              data-testid={`text-sweet-name-${id}`}
            >
              {name}
            </h3>
            <Badge 
              variant="secondary" 
              className="text-xs shrink-0"
              data-testid={`badge-category-${id}`}
            >
              {category}
            </Badge>
          </div>

          {description && (
            <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`text-description-${id}`}>
              {description}
            </p>
          )}

